import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom'
function HomeCard(props) {
  const [arrow, setArrow] = useState(false)
  const change = () => {
    setArrow(!arrow)
  }
  return (
    <div className="BoardsPage">
      <div className="left-navBar">
        <div
          className="left-items"
          style={{ background: '#e4f0f6', width: '50%', fontWeight: 'bold' }}
        >
          <span className="fa fa-trello left-icon "></span>
          <NavLink to="/boards" className="link">
            Boards
          </NavLink>
        </div>
        <div className="left-items">
          <span className="fa fa-home left-icon"></span>
          <NavLink
            to="/home"
            className="link"
            style={{ width: '50%', fontWeight: 'bold' }}
          >
            {''}
            home
          </NavLink>
        </div>

        <div className="drop-down" onClick={change}>
          <span className="fa fa-user"></span>
          <h4 className="left-navbar-dummy">ZopSmart</h4>
          <div className={`down-${arrow}`}>
            <span className={`fa fa-arrow-down`}></span>
          </div>
          <div className={`up-${arrow}`}>
            <span className={`fa fa-arrow-up`}></span>
          </div>
        </div>
      </div>

      <div className="right">
        <div className={`search-item-${props.search}`}>
          <div className="your-Board">
            <strong>
              <span className="fa fa-search" style={{ padding: '1em' }}></span>{' '}
              Search
            </strong>
          </div>
          <div className="container">
            {props.homeData &&
              props.homeData.map((item) => {
                if (
                  props.searchValue &&
                  props.searchValue !== '' &&
                  (item.name.includes(props.searchValue.toLowerCase()) ||
                    item.name.includes(props.searchValue.toUpperCase()))
                ) {
                  return (
                    <div key={item.id}>
                      <NavLink
                        to={`/list/${item.id}`}
                        style={{ textDecoration: 'none' }}
                      >
                        <div
                          className="card"
                          style={{
                            backgroundImage:
                              'url(' + item.prefs.backgroundImage + ')',
                            backgroundColor: item.prefs.backgroundColor,
                            backgroundPosition: 'center',
                            backgroundSize: 'cover',
                            backgroundRepeat: 'no-repeat'
                          }}
                        >
                          <div
                            className="cardName"
                            data-testid="searchBoardName"
                            style={{ padding: '.3em' }}
                          >
                            {item.name}
                          </div>
                        </div>
                      </NavLink>
                    </div>
                  )
                } else {
                  return <div data-testid="noSearch">{''}</div>
                }
              })}
          </div>
        </div>
        <div className="your-Board">
          <strong>
            <span className="fa fa-star" style={{ padding: '1em' }}></span>
            Stared Boards
          </strong>
        </div>
        <div className="container">
          {props.homeData &&
            props.homeData.map((item) => {
              if (item.starred === true) {
                return (
                  <div key={item.id}>
                    <NavLink
                      to={`/list/${item.id}`}
                      style={{ textDecoration: 'none' }}
                    >
                      <div
                        className="card"
                        style={{
                          backgroundImage:
                            'url(' + item.prefs.backgroundImage + ')',
                          backgroundColor: item.prefs.backgroundColor,
                          backgroundPosition: 'center',
                          backgroundSize: 'cover',
                          backgroundRepeat: 'no-repeat'
                        }}
                      >
                        <div
                          className="cardName"
                          data-testid="starredBoard"
                          style={{ padding: '.3em' }}
                        >
                          {item.name}
                        </div>
                      </div>
                    </NavLink>
                  </div>
                )
              } else {
                return ''
              }
            })}
        </div>

        <div className="your-Board">
          <strong>
            <span
              className="fa fa-trello"
              style={{ color: 'blue', padding: '1em' }}
            ></span>
            Personal Boards
          </strong>
        </div>

        <div className="container">
          {props.homeData &&
            props.homeData.map((item) => {
              return (
                <div key={item.id}>
                  <NavLink
                    to={`/list/${item.id}`}
                    data-testid="openList"
                    style={{ textDecoration: 'none' }}
                  >
                    <div
                      className="card"
                      style={{
                        backgroundImage:
                          'url(' + item.prefs.backgroundImage + ')',
                        backgroundColor: item.prefs.backgroundColor,
                        backgroundPosition: 'center',
                        backgroundSize: 'cover',
                        backgroundRepeat: 'no-repeat'
                      }}
                    >
                      <div
                        className="cardName"
                        data-testid="boardName"
                        style={{ padding: '.3em' }}
                      >
                        {item.name}
                      </div>
                    </div>
                  </NavLink>
                </div>
              )
            })}
          <div className="create" onClick={props.openModal}>
            <div className="cardName">
              Create board <span className="fa fa-plus"></span>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
HomeCard.propTypes = {
  homeData: PropTypes.array,
  loading: PropTypes.bool.isRequired,
  openModal: PropTypes.func.isRequired,
  search: PropTypes.bool,
  searchValue: PropTypes.string
}
export default HomeCard
