import React from 'react'
import PropTypes from 'prop-types'

function List(props) {
  const [listData, setListData] = useState([])
  const [loadingList, setLoadingList] = useState(true)
  const [cardData, setCardData] = useState([])
  useEffect(() => {
    if (boardId) {
      const data = async () => {
        const response = await axios.get(`https://api.trello.com/1/boards/${boardId}/lists?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb
        `)
        const card = await axios.get(
          `https://api.trello.com/1/boards/${boardId}/cards?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb`
        )
        console.log('cards ', card.data)
        console.log(response.data)
        setListData(response.data)
        setCardData(card.data)
        setLoadingList(false)
      }
      data()
    }
  }, [boardId])
  return (
    <div className="lists-container">
      {!loadingList
        ? listData.map((item) => {
            return (
              <div className="list" key={item.id}>
                <h3 className="list-title">{item.name}</h3>
                <ul className="list-items">
                  {cardData.map((card) => {
                    if (card.idList === item.id) {
                      return (
                        <li className="inner-cards" key={card.id}>
                          {card.name}
                          <div className="edit-delete">
                            <span
                              className="fa fa-trash trash-icon"
                              onClick={() => props.deleteCard(card.id)}
                            ></span>
                            <span className="fa fa-pencil"></span>
                          </div>
                        </li>
                      )
                    } else {
                      return ''
                    }
                  })}
                </ul>
                <button
                  className="add-card-btn "
                  onClick={() => props.createCard(item.id)}
                >
                  Add a Card
                </button>
              </div>
            )
          })
        : ''}
    </div>
  )
}
List.propTypes = {
  deleteCard: PropTypes.func.isRequired,
  createCard: PropTypes.func.isRequired
}
export default List
