import React, { useEffect, useState } from 'react'
import './Task2.css'
import HomeCard from './Task2Components/HomeCard'
import NavBar from './Task2Components/NavBar'
import Home from './Task2Components/Home'
import Modal from './Task2Components/Modal'
import axios from 'axios'
import List from './Task2Components/List'
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom'

function App() {
  const [homeData, setHomeData] = useState([])
  const [loading, setLoading] = useState(true)
  const [modalState, setModalState] = useState(false)
  const [listData, setListData] = useState([])
  const [boardId, setBoardId] = useState(null)
  const [loadingList, setLoadingList] = useState(true)
  const [boardName, setBoardName] = useState(null)
  useEffect(() => {
    const getData = async () => {
      const response = await axios.get(
        'https://api.trello.com/1/members/me/boards?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb'
      )
      console.log(response.data)
      setHomeData(response.data)
      setLoading(false)
    }
    getData()
  }, [])
  useEffect(() => {
    const data = async () => {
      const response = await axios.get(`https://api.trello.com/1/boards/${boardId}/lists?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb
     `)
      console.log(response.data)
      setListData(response.data)
      setLoadingList(false)
    }
    data()
  }, [boardId])
  const openModal = () => {
    setModalState(!modalState)
  }
  const onHandleChange = (e) => {
    setBoardName(e.target.value)
  }
  const openCard = (id) => {
    setBoardId(id)
  }
  const addItem = async () => {
    console.log(boardName)
    await axios.post(
      `https://api.trello.com/1/boards/?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb&name=${boardName}`
    )
  }
  return (
    <BrowserRouter>
      <div className="app">
        <NavBar openModal={openModal}></NavBar>
        <Modal
          modalState={modalState}
          openModal={openModal}
          onHandleChange={onHandleChange}
          addItem={addItem}
        ></Modal>
        <Switch>
          <Route path="/home" component={Home}></Route>
          <Route
            path="/boards"
            component={() => (
              <HomeCard
                homeData={homeData}
                loading={loading}
                openCard={openCard}
              ></HomeCard>
            )}
          ></Route>
          <Route
            path="/list"
            component={() => (
              <List loadingList={loadingList} listData={listData}></List>
            )}
          ></Route>
          <Redirect to="/home"></Redirect>
        </Switch>
      </div>
    </BrowserRouter>
  )
}
export default App
