import React, { useEffect, useState } from 'react'
import './Task2.css'
import HomeCard from './Task2Components/HomeCard'
import NavBar from './Task2Components/NavBar'
import Home from './Task2Components/Home'
import Modal from './Task2Components/Modal'
import axios from 'axios'
import List from './Task2Components/List'
import EditModal from './Task2Components/EditModal'
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom'

function App() {
  const [homeData, setHomeData] = useState([])
  const [loading, setLoading] = useState(true)
  const [modalState, setModalState] = useState(false)
  const [boardName, setBoardName] = useState(null)
  const [editCardName, setEditCardName] = useState(false)
  const [newCardName, setNewCardName] = useState()
  const [NewCardNameId, setNewCardNameId] = useState()
  useEffect(async () => {
    const response = await axios.get(
      'https://api.trello.com/1/members/me/boards?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb'
    )
    console.log(response.data)
    setHomeData(response.data)
    setLoading(false)
  }, [])
  const openModal = () => {
    setModalState(!modalState)
  }
  const onHandleChange = (e) => {
    setBoardName(e.target.value)
  }
  const addItem = async () => {
    await axios.post(
      `https://api.trello.com/1/boards/?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb&name=${boardName}`
    )
  }
  const activeCard = (id) => {
    setNewCardNameId(id)
  }
  const toggleEditCardName = () => {
    setEditCardName(!editCardName)
  }
  const onEditCardName = (e) => {
    setNewCardName(e.target.value)
  }
  const onEditSubmit = async () => {
    console.log(newCardName)
    await axios.put(
      `https://api.trello.com/1/cards/${NewCardNameId}?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb&name=${newCardName}`
    )
  }

  return (
    <BrowserRouter>
      <div className="app">
        <NavBar openModal={openModal}></NavBar>
        <EditModal
          editCardName={editCardName}
          onEditCardName={onEditCardName}
          onEditSubmit={onEditSubmit}
          activeCard={activeCard}
          toggleEditCardName={toggleEditCardName}
        ></EditModal>
        <Modal
          modalState={modalState}
          openModal={openModal}
          onHandleChange={onHandleChange}
          addItem={addItem}
        ></Modal>
        <Switch>
          <Route path="/home" component={Home}></Route>
          <Route
            path="/boards"
            component={() => (
              <HomeCard
                homeData={homeData}
                loading={loading}
                openModal={openModal}
              ></HomeCard>
            )}
          ></Route>
          <Route
            path="/list/:id"
            component={() => (
              <List
                activeCard={activeCard}
                toggleEditCardName={toggleEditCardName}
              ></List>
            )}
          ></Route>
          <Redirect to="/home"></Redirect>
        </Switch>
      </div>
    </BrowserRouter>
  )
}
export default App
