import React, { useEffect, useState } from 'react'
import axios from 'axios'
function HomeCard() {
  const [homeData, setHomeData] = useState([])
  const [loading, setLoading] = useState(true)
  useEffect(() => {
    const getData = async () => {
      const response = await axios.get(
        'https://api.trello.com/1/members/me/boards?key=d99233bea37b0025a58fa72dc607d4ff&token=e8c437e0e01d12dfdf3abcb5bf7fe9b8f62094009251fc654ff71d1607517edb'
      )
      console.log(response.data)
      setHomeData(response.data)
      setLoading(false)
    }
    getData()
  }, [])
  const addItem = () => {
    useState(...homeData, { id: 1, name: 'ankit' })
  }
  return (
    <div className="container">
      {!loading
        ? homeData.map((item) => {
            return (
              <div key={item.id}>
                <div className="card">
                  <div className="cardName">{item.name}</div>
                </div>
              </div>
            )
          })
        : ''}
      <button className="button" onClick={addItem}></button>
    </div>
  )
}
export default HomeCard
